import 'reflect-metadata'
import React, { useState } from 'react';
import styled from 'styled-components';
import stores from '../stores';
import { List } from 'antd';
import { StoreModel, Task, TimeLine } from '../model/model'
import DateUtils from '../utils/DateUtils'
import Format from '../utils/Format'
import { CurrentTaskKey } from '../stores/CurrentTaskStore';



const Div = styled.div`
height: 45vh;
overflow: scroll;
white-space: pre-wrap;
padding: 5px;
background-color:rgba(194, 226, 255, 0.795);

::-webkit-scrollbar {
            -webkit-appearance: none;
            width: 7px;
        }
        ::-webkit-scrollbar-thumb {
            border-radius: 4px;
            background-color: rgba(0, 0, 0, .5);
            box-shadow: 0 0 1px rgba(255, 255, 255, .5);
        }
`

const TodayWorkConatiner: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };
  const { currentTask, setCurrentTask } = stores.useStore(CurrentTaskKey) as { currentTask: Task | undefined, setCurrentTask: (note: Task) => void }


  function getTimelines(): string {
    return storeModel.getCurrentDayDoingTasks(DateUtils.getMyCurrentDayDur(0))
      .sort((a, b) => {
        if (a.lastStartTime() > b.lastStartTime()) {
          return -1
        } else if (a.lastStartTime() < b.lastStartTime()) {
          return 1
        } else {
          return 0
        }
      }
      )
      .map((task) => {
        return `${task.title} `
      }).join("\n")
  }

  function getCurrentDayDoingTasks(): Task[] {
    return storeModel.getCurrentDayDoingTasks(DateUtils.getMyCurrentDayDur(0))
      .sort((a, b) => {
        if (a.lastStartTime() > b.lastStartTime()) {
          return -1
        } else if (a.lastStartTime() < b.lastStartTime()) {
          return 1
        } else {
          return 0
        }
      }
      )
  }

  return (
    <Div>
      {/* <div style={{ fontWeight: "bold" }}>【最近任务】</div> */}

      <List
        size="small"
        bordered
        dataSource={getCurrentDayDoingTasks()}
        renderItem={task => (
          <div key={task.id} onClick={(e) => onChooseNote(e, task)}>
            {task.title}
          </div>
        )}
      />
    </Div>
  );
}

export default TodayWorkConatiner;
