const TaskListContainer: React.FC = () => {
  const { storeModel, save, setModel } = stores.useStore('mainmodel') as { storeModel: StoreModel, save: () => void, setModel: (storeModel: StoreModel) => void };
  const { currentDayOffset, setCurrentDayOffset } = stores.useStore(CurrentDayOffsetKey) as { currentDayOffset: number, setCurrentDayOffset: (offset: number) => void }
  const [title, setTitle] = useState("");
  const [isNeedExceptTime, setNeedExceptTime] = useState(true);
  const [isCycleTask, setCycleTask] = useState(true);
  const [isLongTask, setLongTask] = useState(true);
  const [isStartNow, setStartNow] = useState(true);
  const [expectTime, setExpectTime] = useState(2);
  const [deadline, setDeadlineUI] = useState<any>(null);

  // React.useEffect(() => {
  //   console.log("TaskListContainer useEffect")
  // },[])

  function onAddNote(event: any) {
    event.preventDefault();

    if (Utils.isStringEmpty(title)) {
      message.error("请输入内容")
      return
    }

    const newTask = new Task()
    newTask.init(title)

    if (isNeedExceptTime) {
      newTask.expectConsumes.push(expectTime * 3600 * 1000) // 单位小时，转换
    } else {
      newTask.expectConsumes.push(-1) // 表示不设置时间
    }

    storeModel.addTask(newTask)
    storeModel.orderTasks()
    save()

    setTitle("")
  }

  function changeOffsetDay(isAdd: boolean) {
    if (isAdd) {
      setCurrentDayOffset(currentDayOffset + 1)
    } else {
      setCurrentDayOffset(currentDayOffset - 1)
    }
  }

  function download() {
    Utils.downloadFile(`DoingList_${Format.formatTimeInMsToFileName(Utils.getTimestamp())}.dl`, JSON.stringify(storeModel))
  }

  function upload(e: any) {
    let file = e.target.files[0];
    console.log(file)

    let reader = new FileReader();
    reader.onload = function (this) {
      let uploadContent = this.result
      if (typeof uploadContent === 'string') {
        Modal.confirm({
          title: '确定要导入嘛？',
          content: `导入会覆盖当前所有任务`,
          onOk() {
            Modal.confirm({
              title: '是否需要先下载当前任务？',
              content: ``,
              onOk() {
                download()
                setModel(plainToClass(StoreModel, JSON.parse(uploadContent as string)))
              },
              onCancel() {
                setModel(plainToClass(StoreModel, JSON.parse(uploadContent as string)))
              }
            });
          },
        })
      } else {
        message.error("导入文件有问题，导入失败")
      }
    }
    //读取文件内容
    reader.readAsText(file);
  }

  function getOffsetString() {
    let base = "" + currentDayOffset
    if (currentDayOffset > 0) {
      base = "+" + base
    }
    return base
  }

  function getAllTaskStatus(): string {
    let notDoneTasks = storeModel.getTasks(DateUtils.getMyCurrentDayDur(currentDayOffset)).filter((task) => { return !task.isDone() })

    if (notDoneTasks.length === 0) {
      return ""
    }

    let consumeFormat = ""
    let consumes: number = notDoneTasks.map((task) => {
      let left = task.getLeftExpectConsumeTime()
      if (left < 0) return 0
      return left
    }).reduce((a, b) => a + b)

    // console.log("getAllTaskStatus" + consumes)
    if (consumes > 0) {
      consumeFormat = `，总剩余预估需要花费${Format.formatDuration(consumes)}`
    }

    return `共${notDoneTasks.length}个任务未完成${consumeFormat}`
  }

  function onChooseDeadline(value: any) {
    // todo
  }

  return (
    <Container>

      {/* todo 这个组件可以单独抽出去 */}
      <ButtonGroup>
        <Button type="primary" size="small" onClick={(e) => changeOffsetDay(false)}>←</Button>

        <span style={{ backgroundColor: "#ffffff", padding: 4 }} >
          {Format.formatTimeInDay(DateUtils.getMyCurrentDayStamp(currentDayOffset))}({getOffsetString()})
        </span>

        <Button type="primary" size="small" onClick={(e) => changeOffsetDay(true)}>→</Button>
        <span style={{ paddingLeft: 10 }} ></span>
        <Button type="primary" size="small" onClick={(e) => download()}>导出</Button>
        <input type="file" id="upload-file" multiple onChange={(event) => upload(event)} />
        {/* <Button type="primary" size="small" onClick={(e) => download()}>导入</Button> */}
      </ButtonGroup>

      <Row style={{ margin: "10px" }}>
        <Col flex="1 1 auto">
          <Input placeholder="输入任务，回车添加" value={title} onChange={(event) => setTitle(event.target.value)} onPressEnter={onAddNote} />
        </Col>
        <Col flex="0 1 auto">

          <Switch style={{ marginLeft: '5px', marginRight: '3px' }} checkedChildren="估时" unCheckedChildren="不限" defaultChecked={isNeedExceptTime} onChange={(event) => setNeedExceptTime(event)} />


          <Switch style={{ marginLeft: '5px', marginRight: '3px' }} checkedChildren="周期" unCheckedChildren="单个" defaultChecked={isCycleTask} onChange={(event) => setCycleTask(event)} />

          {/* todo 长任务会默认打开估时 */}
          <Switch style={{ marginLeft: '5px', marginRight: '3px' }} checkedChildren="长任务" unCheckedChildren="短任务" defaultChecked={isLongTask} onChange={(event) => setLongTask(event)} />

          <Switch style={{ marginLeft: '5px', marginRight: '3px' }} checkedChildren="立刻开始" unCheckedChildren="之后开始" defaultChecked={isStartNow} onChange={(event) => setStartNow(event)} />

        </Col>
      </Row>

      {isNeedExceptTime &&
      <Div style={{ margin: "10px", color: "#ffffff" }}>

      <Input style={{ width: '100px' }} defaultValue={expectTime} placeholder="花费时间" prefix="预计" suffix="h" onChange={(event) => setExpectTime(Number(event.target.value))} onPressEnter={onAddNote} />
    </Div>

          }

{isStartNow &&
      <Div style={{ margin: "10px", color: "#ffffff" }}>

      <Input style={{ width: '100px' }} defaultValue={expectTime} placeholder="花费时间" prefix="预计" suffix="h" onChange={(event) => setExpectTime(Number(event.target.value))} onPressEnter={onAddNote} />
    </Div>

          }

      {isCycleTask &&
        <Div style={{ margin: "10px", color: "#ffffff" }}>
          <Input style={{ width: '130px' }} defaultValue={expectTime} placeholder="0表示不间隔" prefix="每隔" suffix="天" onChange={(event) => setExpectTime(Number(event.target.value))} onPressEnter={onAddNote} />
          <span style={{ marginLeft: "15px" }} >开始时间：</span>
          <DatePicker showTime value={deadline} onOk={value => onChooseDeadline(value)} />
        </Div>
      }

      {isLongTask &&
        <Div style={{ margin: "10px", color: "#ffffff" }}>
          长任务：
          <Input style={{ width: '100px' }} defaultValue={expectTime}  prefix="计划" suffix="天" onChange={(event) => setExpectTime(Number(event.target.value))} onPressEnter={onAddNote} />
          <Input style={{ width: '100px' }} defaultValue={expectTime}  prefix="假期" suffix="天" onChange={(event) => setExpectTime(Number(event.target.value))} onPressEnter={onAddNote} />
        </Div>
      }

      <span style={{ backgroundColor: "#ffffff", padding: 2, margin: "10px" }} >
        {getAllTaskStatus()}
      </span>

      <NoteList />
    </Container>
  );
}


