import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Input, message, Button, List, Dropdown, Menu, Modal, DatePicker } from 'antd';
import stores from '../stores';
import { Task } from '../model/model'
import { StoreModel, Duration } from '../model/model'
import Utils from '../utils/Utils'
import DateUtils from '../utils/DateUtils'
import Format from '../utils/Format'
import { CurrentTaskKey } from '../stores/CurrentTaskStore';
import 'antd/dist/antd.css';
import { CurrentDoingTaskKey } from '../stores/CurrentDoingTaskStore';
import { ClickParam } from 'antd/lib/menu';
// import { DateType } from 'antd/lib/date-picker';
import { DownOutlined } from '@ant-design/icons';
import moment from 'moment'

const Container = styled.div`
  background: #ffffff;
  display: flex;
  height: 100%;
  flex-direction: column;
`

const Div = styled.div`
`

const Editor = styled(Input.TextArea)`
  flex-grow: 1;
`

const ButtonGroup = styled(Button.Group)`
margin: 2px;
`

const ContentLayout: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };
  const { currentTask } = stores.useStore(CurrentTaskKey) as { currentTask: Task | undefined }
  const { save } = stores.useStore('mainmodel') as { save: () => void };
  const [content, setContent] = useState("")

  // toolsbar
  const { currentDoingTask, setCurrentDoingTask } = stores.useStore(CurrentDoingTaskKey) as { currentDoingTask: Task | undefined, setCurrentDoingTask: (note: Task | undefined) => void }

  /* ---------------------------åˆå§‹åŒ–------------------------------- */

  // æ¸²æŸ“å½“å‰note
  const [lastTask, setLastTask] = useState<Task | undefined>(undefined)
  useEffect(() => {
    // console.log(`Current content:${content}`)
    if (currentTask === undefined) return

    if (!currentTask.isSameTask(lastTask)) {
      // todo è¿™é‡Œæ”¾åŒæ­¥åº”è¯¥ä¸ä¼šæœ‰é—®é¢˜å§ï¼Œæœ€å¤§çš„é—®é¢˜åº”è¯¥æ˜¯å‰é¢åˆ‡äº†noteä½†æ²¡åˆ‡contentçš„æ—¶å€™è¿›è¡Œä¿å­˜...
      setLastTask(currentTask)
      setContent(currentTask.note.content)
    }
  })

  /* ----------------------------ä¿å­˜Noteæ–‡æœ¬------------------------------ */

  function onBlur(event: any) {
    event.preventDefault();
    saveToLocal(event, false)
  }

  function saveToLocal(event: any, isManual: boolean) {
    if (event) event.preventDefault();
    if (currentTask === undefined) return

    currentTask.note.content = content
    save()

    if (isManual) message.info("å·²ä¿å­˜")
  }

  /* ------------------------------toolsbar---------------------------- */

  function deleteTask(event: any) {
    if (currentTask === undefined) return

    Modal.confirm({
      title: 'ç¡®å®šè¦åˆ é™¤å—ï¼Ÿ',
      content: `æ˜¯å¦åˆ é™¤ä»»åŠ¡ï¼š${currentTask.title}`,
      onOk() {
        if (storeModel.removeTask(currentTask)) {
          message.success("åˆ é™¤ä»»åŠ¡æˆåŠŸ")
          save()
        } else {
          message.error("åˆ é™¤ä»»åŠ¡å¤±è´¥")
        }
      },
    });
  }

  function doneTask(event: any) {
    if (currentTask === undefined) return
    const task = currentTask

    if (task.isDone()) {
      message.error(`ä»»åŠ¡å·²å®Œæˆï¼Œå®Œæˆæ—¶é—´:${Format.formatTimeInMs(task.doneTime)}`)
      return
    }

    if (!task.isDoing()) {
      Modal.confirm({
        title: 'å½“å‰ä»»åŠ¡ä¸åœ¨è¿›è¡Œä¸­ï¼Œç¡®å®šè¦å®Œæˆå—ï¼Ÿ',
        content: `æ˜¯å¦å®Œæˆä»»åŠ¡ï¼š${task.title}`,
        onOk() {
          _realDone(task)
        },
      });
      return
    }

    task.stop()
    setCurrentDoingTask(undefined)
    _realDone(task)
  }

  function _realDone(task: Task) {
    task.done()
    storeModel.orderTasks()
    message.success("ä»»åŠ¡å·²å®Œæˆ")
    save()
  }

  function startTask(event: any) {
    if (currentTask === undefined) return
    const task = currentTask

    if (task.isDone()) {
      message.error(`ä»»åŠ¡å·²å®Œæˆï¼Œå®Œæˆæ—¶é—´:${Format.formatTimeInMs(task.doneTime)}`)
      return
    }

    if (currentDoingTask != undefined && currentDoingTask.id === task.id) {
      message.error(`ä»»åŠ¡å·²ç»åœ¨è¿›è¡Œä¸­ï¼Œä¸Šæ¬¡å¼€å§‹æ—¶é—´ï¼š${Format.formatTimeInMs(task.lastStartTime())}`)
      return
    }

    if (task.isDoing()) {
      // todo è‡ªåŠ¨æ¢å¤
      message.error(`ä»»åŠ¡å¼‚å¸¸ä¸­æ–­ï¼Œé‡æ–°å¼€å§‹ã€‚ä¸Šæ¬¡å¼€å§‹æ—¶é—´ï¼š${Format.formatTimeInMs(task.lastStartTime())}`)
    }

    if (currentDoingTask != undefined) {
      currentDoingTask.stop()
    }

    task.start()
    setCurrentDoingTask(task)
    save()
  }

  const onClickMenu = (param: ClickParam, task: Task) => {
    task.level = Number(param.key)

    storeModel.orderTasks()
    save()

    param.domEvent.preventDefault()

    // æ€ä¹ˆé˜»æ­¢å¯¹ parent çš„ç‚¹å‡»äº‹ä»¶
    // param.domEvent.stopPropagation()
  };

  function onChooseDeadline(value: any) {
    if (currentTask === undefined) return
    const task = currentTask

    // æ•´äº†åŠå¤©ï¼ŒåŸæ¥ç›´æ¥ valueOf() å°±è¡Œï¼Œç™¾åº¦äº†åŠå¤©
    // if(value instanceof Moment){
    //   let moment:Moment = value
    //   console.log('onOk: ', moment.);
    // }
    // console.log('onOk: ', value.valueOf());

    task.deadlines.push(value.valueOf())
  }

  function getDeadlineTime(): any {
    if (currentTask === undefined) return undefined
    const task = currentTask

    const deadline = task.getActualDeadline();
    if (deadline > 0) {
      return moment(deadline)
    }

    return undefined
  }


  function getDoingStatusTips(task: Task): string {
    if (task.isDoing()) {
      return "æ–½å·¥ä¸­â›‘ "
    } else {
      return ""
    }
  }

  function getConsumed(task: Task): string {
    let time = Format.formatDuration(task.getAllDuration());
    if (Utils.isStringEmpty(time)) {
      return ""
    }
    return `è¿›è¡Œ${time}(${task.doingDurs.length}æ¬¡) `
  }

  function getDeadline(task: Task): string {
    let deadline = task.getActualDeadline()
    if (deadline <= 0) {
      return ""
    }
    const leftTime = deadline - Utils.getTimestamp();
    if (leftTime <= 1000) {
      return `å·²è¶…æœŸğŸ˜± `
    }
    let time = Format.formatDuration(leftTime);
    return `å‰©ä½™${time} `
  }

  function getConsumeExpected(task: Task): string {
    if (task.getActualExpectConsume() <= 0) {
      return ""
    }

    let left = task.getLeftExpectConsumeTime()
    if (left <= 0) {
      return `è¶…å‡º${Format.formatDuration(-left)}`
    } else {
      return `è¿˜éœ€${Format.formatDuration(left)}`
    }
  }

  function getShowTips(): string {
    if (currentTask === undefined) return ""
    const task = currentTask

    let result = "  " + getDoingStatusTips(task) + getConsumed(task) + getDeadline(task) + getConsumeExpected(task)
    return result
  }

  /* ---------------------------------------------------------- */

  return (
    <Container>
      {/* todo æ²¡æœ‰ currentTask å°±ä¸å±•ç¤º */}

      {/* ä¸åŠ è¿™ä¸ª div ä¼šæ¢è¡Œ */}
      <Div>
      <ButtonGroup>
        {/* ç‚¹å‡»æ•´è¡Œå°±æ˜¯æŸ¥çœ‹ä¸”å¼€å§‹ï¼Œviewæ˜¯åªæŸ¥çœ‹ä¸å¼€å§‹ã€‚æš‚æ—¶ç”¨ä¸ç€å§ */}
        {/* <Button type="primary" size="small">view</Button> */}
        <Button onClick={(e) => deleteTask(e)}>D</Button>
        {/* <Button type="primary" size="small" onClick={(e) => startTask(e, task)}>Stop</Button> */}
        <Button onClick={(e) => doneTask(e)}>Done</Button>
        <Button type="primary" onClick={(e) => startTask(e)}>Start</Button>

      </ButtonGroup>

      <DatePicker showTime defaultValue={getDeadlineTime()} onOk={value => onChooseDeadline(value)} />
      {getShowTips()}
      </Div>

      <Editor
        placeholder="ä»»åŠ¡æ—¥å¿—ï¼ˆè‡ªåŠ¨ä¿å­˜ï¼‰" value={content} onChange={(event) => setContent(event.target.value)} onBlur={(e) => onBlur(e)}
      />
    </Container>
  );
}

export default ContentLayout;
