import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Input, message, Button, Modal } from 'antd';
import stores from '../stores';
import { Task } from '../model/model'
import { StoreModel, Duration } from '../model/model'
import Utils from '../utils/Utils'
import DateUtils from '../utils/DateUtils'
import Format from '../utils/Format'
import { CurrentTaskKey } from '../stores/CurrentTaskStore';
import 'antd/dist/antd.css';
import { CurrentDoingTaskKey } from '../stores/CurrentDoingTaskStore';

const Container = styled.div`
  background: #ffffff;
  display: flex;
  height: 100%;
  flex-direction: column;
`

const Editor = styled(Input.TextArea)`
  flex-grow: 1;
`

const ButtonGroup = styled(Button.Group)`
margin: 2px;
`

const ContentLayout: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };
  const { currentTask } = stores.useStore(CurrentTaskKey) as { currentTask: Task | undefined }
  const { save } = stores.useStore('mainmodel') as { save: () => void };
  const [content, setContent] = useState("")

  // toolsbar
  const { currentDoingTask, setCurrentDoingTask } = stores.useStore(CurrentDoingTaskKey) as { currentDoingTask: Task | undefined, setCurrentDoingTask: (note: Task | undefined) => void }

  /* ---------------------------初始化------------------------------- */

  // 渲染当前note
  const [lastTask, setLastTask] = useState<Task | undefined>(undefined)
  useEffect(() => {
    // console.log(`Current content:${content}`)
    if (currentTask === undefined) return

    if (!currentTask.isSameTask(lastTask)) {
      // todo 这里放同步应该不会有问题吧，最大的问题应该是前面切了note但没切content的时候进行保存...
      setLastTask(currentTask)
      setContent(currentTask.note.content)
    }
  })

  /* ----------------------------保存Note文本------------------------------ */

  function onBlur(event: any) {
    event.preventDefault();
    saveToLocal(event, false)
  }

  function saveToLocal(event: any, isManual: boolean) {
    if (event) event.preventDefault();
    if (currentTask === undefined) return

    currentTask.note.content = content
    save()

    if (isManual) message.info("已保存")
  }

  /* ---------------------------------------------------------- */

  function deleteTask(event: any) {
    if (currentTask === undefined) return

    Modal.confirm({
      title: '确定要删除吗？',
      content: `是否删除任务：${currentTask.title}`,
      onOk() {
        if (storeModel.removeTask(currentTask)) {
          message.success("删除任务成功")
          save()
        } else {
          message.error("删除任务失败")
        }
      },
    });
  }

  function doneTask(event: any, task: Task) {
    if (task.isDone()) {
      message.error(`任务已完成，完成时间:${Format.formatTimeInMs(task.doneTime)}`)
      return
    }

    if (!task.isDoing()) {
      Modal.confirm({
        title: '当前任务不在进行中，确定要完成吗？',
        content: `是否完成任务：${task.title}`,
        onOk() {
          _realDone(task)
        },
      });
      return
    }

    task.stop()
    setCurrentDoingTask(undefined)
    _realDone(task)
  }

  function _realDone(task: Task) {
    task.done()
    storeModel.orderTasks()
    message.success("任务已完成")
    save()
  }

  /* ---------------------------------------------------------- */

  return (
    <Container>
      <ButtonGroup>
              {/* 点击整行就是查看且开始，view是只查看不开始。暂时用不着吧 */}
              {/* <Button type="primary" size="small">view</Button> */}
              <Button size="small" onClick={(e) => deleteTask(e)}>D</Button>
              {/* <Button type="primary" size="small" onClick={(e) => startTask(e, task)}>Stop</Button> */}
              <Button size="small" onClick={(e) => doneTask(e, task)}>Done</Button>
              <Button size="small" onClick={(e) => startTask(e, task)}>Start</Button>

              <Dropdown  overlay={getMenu(task)}>
                <a style={{ paddingLeft: 5 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                  {formartLevel(task.level)} <DownOutlined />
                </a>
              </Dropdown>

            </ButtonGroup>

      <Editor
        placeholder="任务日志（自动保存）" value={content} onChange={(event) => setContent(event.target.value)} onBlur={(e) => onBlur(e)}
      />
    </Container>
  );
}

export default ContentLayout;
