import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Input, message, Switch, Button, Row, Col, List, Dropdown, Menu, Modal, DatePicker } from 'antd';
// è¿™æ ·å±…ç„¶çœŸçš„è¡Œ
import { ClickParam } from 'antd/lib/menu';
// import { DateType } from 'antd/lib/date-picker';
import { DownOutlined } from '@ant-design/icons';
import stores from '../stores';
import { StoreModel, Duration } from '../model/model'
import Utils from '../utils/Utils'
import DateUtils from '../utils/DateUtils'
import TaskInfoFormat from '../utils/TaskInfoFormat'
import Format from '../utils/Format'
import { Task, Note } from '../model/model'
import { CurrentTaskKey } from '../stores/CurrentTaskStore';
import { CurrentDoingTaskKey } from '../stores/CurrentDoingTaskStore';
import { CurrentDayOffsetKey } from '../stores/CurrentDayOffsetStore';
import 'antd/dist/antd.css';
import { plainToClass } from "class-transformer";

const Container = styled.div`
  /* background: #d1d3a2; */
  /* background: #d1dfff; */
  background: #282c34;
`

const Container2 = styled.div`
  /* background: #d1d3a2; */
  /* background: #d1dfff; */
  background: #282c34;
  overflow:auto;
  /* height: 800px; */
  height: 85vh;
  margin-top:10px;
`

const InputGroup = styled(Input.Group)`
margin: 1%;
`
const ButtonGroup = styled(Button.Group)`
margin: 1%;
`

const TaskListContainer: React.FC = () => {
  const { storeModel, save, setModel } = stores.useStore('mainmodel') as { storeModel: StoreModel, save: () => void, setModel: (storeModel: StoreModel) => void };
  const { currentDayOffset, setCurrentDayOffset } = stores.useStore(CurrentDayOffsetKey) as { currentDayOffset: number, setCurrentDayOffset: (offset: number) => void }
  const [title, setTitle] = useState("");
  const [isNeedExceptTime, setNeedExceptTime] = useState(true);
  const [expectTime, setExpectTime] = useState(2);

  // React.useEffect(() => {
  //   console.log("TaskListContainer useEffect")
  // },[])

  function onAddNote(event: any) {
    event.preventDefault();

    if (Utils.isStringEmpty(title)) {
      message.error("è¯·è¾“å…¥å†…å®¹")
      return
    }

    const newTask = new Task()
    newTask.init(title)

    if (isNeedExceptTime) {
      newTask.expectConsumes.push(expectTime * 3600 * 1000) // å•ä½å°æ—¶ï¼Œè½¬æ¢
    } else {
      newTask.expectConsumes.push(-1) // è¡¨ç¤ºä¸è®¾ç½®æ—¶é—´
    }

    storeModel.addTask(newTask)
    storeModel.orderTasks()
    save()

    setTitle("")
  }

  function changeOffsetDay(isAdd: boolean) {
    if (isAdd) {
      setCurrentDayOffset(currentDayOffset + 1)
    } else {
      setCurrentDayOffset(currentDayOffset - 1)
    }
  }

  function download() {
    Utils.downloadFile(`DoingList_${Format.formatTimeInMsToFileName(Utils.getTimestamp())}.dl`, JSON.stringify(storeModel))
  }

  function upload(e: any) {
    let file = e.target.files[0];
    console.log(file)

    let reader = new FileReader();
    reader.onload = function (this) {
      let uploadContent = this.result
      if (typeof uploadContent === 'string') {
        Modal.confirm({
          title: 'ç¡®å®šè¦å¯¼å…¥å˜›ï¼Ÿ',
          content: `å¯¼å…¥ä¼šè¦†ç›–å½“å‰æ‰€æœ‰ä»»åŠ¡`,
          onOk() {
            Modal.confirm({
              title: 'æ˜¯å¦éœ€è¦å…ˆä¸‹è½½å½“å‰ä»»åŠ¡ï¼Ÿ',
              content: ``,
              onOk() {
                download()
                setModel(plainToClass(StoreModel, JSON.parse(uploadContent as string)))
              },
              onCancel() {
                setModel(plainToClass(StoreModel, JSON.parse(uploadContent as string)))
              }
            });
          },
        })
      } else {
        message.error("å¯¼å…¥æ–‡ä»¶æœ‰é—®é¢˜ï¼Œå¯¼å…¥å¤±è´¥")
      }
    }
    //è¯»å–æ–‡ä»¶å†…å®¹
    reader.readAsText(file);
  }

  function getOffsetString() {
    let base = "" + currentDayOffset
    if (currentDayOffset > 0) {
      base = "+" + base
    }
    return base
  }

  function getAllTaskStatus(): string {
    let notDoneTasks = storeModel.getTasks(DateUtils.getMyCurrentDayDur(currentDayOffset)).filter((task) => { return !task.isDone() })

    if (notDoneTasks.length === 0) {
      return ""
    }

    let consumeFormat = ""
    let consumes: number = notDoneTasks.map((task) => {
      let left = task.getLeftExpectConsumeTime()
      if (left < 0) return 0
      return left
    }).reduce((a, b) => a + b)

    // console.log("getAllTaskStatus" + consumes)
    if (consumes > 0) {
      consumeFormat = `ï¼Œæ€»å‰©ä½™é¢„ä¼°éœ€è¦èŠ±è´¹${Format.formatDuration(consumes)}`
    }

    return `å…±${notDoneTasks.length}ä¸ªä»»åŠ¡æœªå®Œæˆ${consumeFormat}`
  }

  return (
    <Container>

      {/* todo è¿™ä¸ªç»„ä»¶å¯ä»¥å•ç‹¬æŠ½å‡ºå» */}
      <ButtonGroup>
        <Button type="primary" size="small" onClick={(e) => changeOffsetDay(false)}>â†</Button>

        {/* è¿™ä¸ªå–å½“å‰æ—¶é—´çš„æ“ä½œç®€ç›´æ— æ•Œ... è ¢ */}
        {/* <DatePicker defaultValue={moment(moment().format(dateFormat), dateFormat)} format={dateFormat} /> */}

        <span style={{ backgroundColor: "#ffffff", padding: 4 }} >
          {Format.formatTimeInDay(DateUtils.getMyCurrentDayStamp(currentDayOffset))}({getOffsetString()})
        </span>

        <Button type="primary" size="small" onClick={(e) => changeOffsetDay(true)}>â†’</Button>
        <span style={{ paddingLeft: 10 }} ></span>
        <Button type="primary" size="small" onClick={(e) => download()}>å¯¼å‡º</Button>
        <input type="file" id="upload-file" multiple onChange={(event) => upload(event)} />
        {/* <Button type="primary" size="small" onClick={(e) => download()}>å¯¼å…¥</Button> */}
      </ButtonGroup>

      <Input.Group compact style={{ width: '96%', margin: '2%', backgroundColor: "#ffffff" }}>
        <Input style={{ width: '60%' }} placeholder="è¾“å…¥ä»»åŠ¡ï¼Œå›è½¦æ·»åŠ " value={title} onChange={(event) => setTitle(event.target.value)} onPressEnter={onAddNote} />
        <Switch checkedChildren="ä¼°æ—¶" unCheckedChildren="ä¸ä¼°æ—¶" defaultChecked={isNeedExceptTime} onChange={(event) => setNeedExceptTime(event)} />
        <Input style={{ width: '20%' }} defaultValue={expectTime} placeholder="èŠ±è´¹æ—¶é—´" prefix="é¢„è®¡" suffix="h" onChange={(event) => setExpectTime(Number(event.target.value))} onPressEnter={onAddNote} />
      </Input.Group>



      <span style={{ backgroundColor: "#ffffff", padding: 4, margin: '2%' }} >
        {getAllTaskStatus()}
      </span>

      <NoteList />
    </Container>
  );
}


const NoteItem = styled.div`
:hover{
	/* background-color:#b4e7fc; */
  background-color: ${(props: { choosed: boolean, isDone: boolean }) => {
    if (props.choosed) {
      return '#f5b52b'
    } else {
      return '#b4e7fc'
    }
  }
  }
};
background-color: ${(props: { choosed: boolean, isDone: boolean }) => {
    if (props.choosed) {
      return '#f5b52b'
    } else {
      return '#ffffff'
    }
  }
  };
opacity : ${(props: { choosed: boolean, isDone: boolean }) => {
    if (props.isDone) {
      return 0.15
    } else {
      return 1
    }
  }
  };;
`
const NoteList: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };
  const { currentTask, setCurrentTask } = stores.useStore(CurrentTaskKey) as { currentTask: Task | undefined, setCurrentTask: (note: Task) => void }
  const { currentDoingTask, setCurrentDoingTask } = stores.useStore(CurrentDoingTaskKey) as { currentDoingTask: Task | undefined, setCurrentDoingTask: (note: Task | undefined) => void }
  const { save } = stores.useStore('mainmodel') as { save: () => void };
  const { currentDayOffset, setCurrentDayOffset } = stores.useStore(CurrentDayOffsetKey) as { currentDayOffset: number, setCurrentDayOffset: (offset: number) => void }

  useEffect(() => {
    // console.log("NoteList useEffect")

    if (currentDoingTask === undefined) {
      let doingTasks = storeModel.tasks.filter((value) => { return value.isDoing() })

      if (doingTasks.length === 0) {
        return
      }

      if (doingTasks.length > 1) {
        console.error(`æœ‰ ${doingTasks.length} ä¸ªä»»åŠ¡åœ¨è¿è¡Œä¸­ï¼Œè‡ªåŠ¨å…³é—­`)

        for (let i = 1; i < doingTasks.length; i++) {
          doingTasks[i].stop()
        }
      }

      setCurrentDoingTask(doingTasks[0])
    }
  });

  function onChooseNote(event: any, note: Task) {
    event.preventDefault()
    setCurrentTask(note)
  }

  const onClickMenu = (param: ClickParam, task: Task) => {
    task.level = Number(param.key)

    storeModel.orderTasks()
    save()

    param.domEvent.preventDefault()

    // æ€ä¹ˆé˜»æ­¢å¯¹ parent çš„ç‚¹å‡»äº‹ä»¶
    // param.domEvent.stopPropagation()
  };

  function getMenu(task: Task): React.ReactElement {
    return (
      <Menu onClick={(param) => onClickMenu(param, task)}>
        <Menu.Item key="1">ä¼˜å…ˆçº§ 1</Menu.Item>
        <Menu.Item key="2">ä¼˜å…ˆçº§ 2</Menu.Item>
        <Menu.Item key="3">ä¼˜å…ˆçº§ 3</Menu.Item>
        <Menu.Item key="4">ä¼˜å…ˆçº§ 4</Menu.Item>
        <Menu.Item key="5">ä¼˜å…ˆçº§ 5</Menu.Item>
      </Menu>
    )
  }

  function startTask(event: any, task: Task) {
    if (task.isDone()) {
      message.error(`ä»»åŠ¡å·²å®Œæˆï¼Œå®Œæˆæ—¶é—´:${Format.formatTimeInMs(task.doneTime)}`)
      return
    }

    if (currentDoingTask != undefined && currentDoingTask.id === task.id) {
      message.error(`ä»»åŠ¡å·²ç»åœ¨è¿›è¡Œä¸­ï¼Œä¸Šæ¬¡å¼€å§‹æ—¶é—´ï¼š${Format.formatTimeInMs(task.lastStartTime())}`)
      return
    }

    if (task.isDoing()) {
      // todo è‡ªåŠ¨æ¢å¤
      message.error(`ä»»åŠ¡å¼‚å¸¸ä¸­æ–­ï¼Œé‡æ–°å¼€å§‹ã€‚ä¸Šæ¬¡å¼€å§‹æ—¶é—´ï¼š${Format.formatTimeInMs(task.lastStartTime())}`)
    }

    if (currentDoingTask != undefined) {
      currentDoingTask.stop()
    }

    task.start()
    setCurrentDoingTask(task)
    storeModel.orderTasks()
    save()
  }


  function formartLevel(level: number): string {
    switch (level) {
      case 1:
        return "â—"
      case 2:
        return "ğŸ"
      case 3:
        return "3ï¸âƒ£"
      case 4:
        return "4ï¸"
      case 5:
        return "5ï¸"
      default:
        return ""
    }
  }

  function getShowTips(task: Task): string {
    let result = "  " + TaskInfoFormat.getDoingStatusTips(task) + TaskInfoFormat.getDeadline(task) + TaskInfoFormat.getConsumeExpected(task)
    return result
  }

  return (
    <Container2>
      <List
        size="small"
        style={{ backgroundColor: 'white', margin: "1%" }}
        bordered
        dataSource={storeModel.getTasks(DateUtils.getMyCurrentDayDur(currentDayOffset))}
        renderItem={task => (
          <NoteItem choosed={task.isSameTask(currentTask)} key={task.id} onClick={(e) => onChooseNote(e, task)} isDone={task.isDone()}>

            <Row>
              <Col flex="1 1 auto">
                <NoteItemInput key={task.id} task={task} />
              </Col>
              <Col flex="0 1 auto">

                {getShowTips(task)}
                <Dropdown overlay={getMenu(task)}>
                  <a style={{ paddingLeft: 5 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                    {formartLevel(task.level)} <DownOutlined />
                  </a>
                </Dropdown>

                <Button  onClick={(e) => startTask(e, task)}>Start</Button>

              </Col>
            </Row>

          </NoteItem>
        )}
      />
    </Container2>
  );
}

interface NoteItemInputProps {
  task: Task;
}

const NoteItemInput: React.FC<NoteItemInputProps> = (props) => {
  const [content, setContent] = useState(props.task.title)
  const { save } = stores.useStore('mainmodel') as { save: () => void };

  function saveTaskTitle(event: any) {
    props.task.title = content
    save()
  }

  return (
    <Input style={{ background: "#ffffff70", fontWeight: "bold" }} value={content} onChange={(e) => setContent(e.target.value)} onBlur={saveTaskTitle} />
  );
}

export default TaskListContainer;
