import { Duration } from "../model/model";

class DateUtils {
    private static dayMills: number = 24 * 60 * 60 * 1000
    private static dayOffset: number = 6

    // 获得今天某个小时的时间戳
    public static getCurrentDayStamp(hour: number): number {
        let today = new Date();
        today.setHours(hour, 0, 0, 0);
        return today.getTime();
    }

    // 获得某天某个小时的时间戳
    // 暂时 offsetHour 只支持 0-24 之间的数
    public static getOffsetedDayDur(dayZeroTime: number, offsetHour: number): Duration {
        let day = new Date(dayZeroTime)
        day.setHours(offsetHour, 0, 0, 0)

        let result = new Duration()
        result.init(day.getTime())
        result.end = day.getTime() + DateUtils.dayMills
        return result
    }

    //-------------------- 以下业务相关 --------------------

    /**
     * 获得今天（是否当天按6点判断） offset 某些天的 0 点时间戳
     */
    public static getMyCurrentDayStamp(offsetDay: number): number {
        let now = new Date().getTime()
        // 今天6点
        let todayOffset = DateUtils.getCurrentDayStamp(DateUtils.dayOffset)

        if (now > todayOffset) {
            return DateUtils.getCurrentDayStamp(0) + DateUtils.dayMills * offsetDay
        } else {
            return DateUtils.getCurrentDayStamp(0) - DateUtils.dayMills + DateUtils.dayMills * offsetDay
        }
    }

    public static getMyCurrentDayDur(offsetDay: number): Duration {
        return DateUtils.getOffsetedDayDur(DateUtils.getMyCurrentDayStamp(offsetDay), DateUtils.dayOffset)
    }
}

export default DateUtils
