background-color: ${(props: { choosed: boolean, isDone: boolean }) => {
    if (props.choosed) {
      return '#f5b52b'
    } else {
      return '#ffffff'
    }
  }
  };
:hover{
	background-color:#b4e7fc;
};
opacity : ${(props: { choosed: boolean, isDone: boolean }) => {
    if (props.isDone) {
      return 0.15
    } else {
      return 1
    }
  }
  };;
`
const NoteList: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };
  const { currentTask, setCurrentTask } = stores.useStore(CurrentTaskKey) as { currentTask: Task | undefined, setCurrentTask: (note: Task) => void }
  const { currentDoingTask, setCurrentDoingTask } = stores.useStore(CurrentDoingTaskKey) as { currentDoingTask: Task | undefined, setCurrentDoingTask: (note: Task | undefined) => void }
  const { save } = stores.useStore('mainmodel') as { save: () => void };
  const { currentDayOffset, setCurrentDayOffset } = stores.useStore(CurrentDayOffsetKey) as { currentDayOffset: number, setCurrentDayOffset: (offset: number) => void }

  useEffect(() => {
    // console.log("NoteList useEffect")

    if (currentDoingTask === undefined) {
      let doingTasks = storeModel.tasks.filter((value) => { return value.isDoing() })

      if (doingTasks.length === 0) {
        return
      }

      if (doingTasks.length > 1) {
        console.error(`有 ${doingTasks.length} 个任务在运行中，自动关闭`)

        for (let i = 1; i < doingTasks.length; i++) {
          doingTasks[i].stop()
        }
      }

      setCurrentDoingTask(doingTasks[0])
    }
  });

  function onChooseNote(event: any, note: Task) {
    event.preventDefault()
    setCurrentTask(note)
  }

  const onClickMenu = (param: ClickParam, task: Task) => {
    task.level = Number(param.key)

    storeModel.orderTasks()
    save()

    param.domEvent.preventDefault()

    // 怎么阻止对 parent 的点击事件
    // param.domEvent.stopPropagation()
  };

  function getMenu(task: Task): React.ReactElement {
    return (
      <Menu onClick={(param) => onClickMenu(param, task)}>
        <Menu.Item key="1">优先级 1</Menu.Item>
        <Menu.Item key="2">优先级 2</Menu.Item>
        <Menu.Item key="3">优先级 3</Menu.Item>
        <Menu.Item key="4">优先级 4</Menu.Item>
        <Menu.Item key="5">优先级 5</Menu.Item>
      </Menu>
    )
  }

  function deleteTask(event: any, task: Task) {
    Modal.confirm({
      title: '确定要删除吗？',
      content: `是否删除任务：${task.title}`,
      onOk() {
        if (storeModel.removeTask(task)) {
          message.success("删除任务成功")
          save()
        } else {
          message.error("删除任务失败")
        }
      },
    });
  }

  function startTask(event: any, task: Task) {
    if (task.isDone()) {
      message.error(`任务已完成，完成时间:${Format.formatTimeInMs(task.doneTime)}`)
      return
    }

    if (currentDoingTask != undefined && currentDoingTask.id === task.id) {
      message.error(`任务已经在进行中，上次开始时间：${Format.formatTimeInMs(task.lastStartTime())}`)
      return
    }

    if (task.isDoing()) {
      // todo 自动恢复
      message.error(`任务异常中断，重新开始。上次开始时间：${Format.formatTimeInMs(task.lastStartTime())}`)
    }

    if (currentDoingTask != undefined) {
      currentDoingTask.stop()
    }

    task.start()
    setCurrentDoingTask(task)
    save()
  }

  function doneTask(event: any, task: Task) {
    if (task.isDone()) {
      message.error(`任务已完成，完成时间:${Format.formatTimeInMs(task.doneTime)}`)
      return
    }

    if (!task.isDoing()) {
      Modal.confirm({
        title: '当前任务不在进行中，确定要完成吗？',
        content: `是否完成任务：${task.title}`,
        onOk() {
          _realDone(task)
        },
      });
      return
    }

    task.stop()
    setCurrentDoingTask(undefined)
    _realDone(task)
  }

  function _realDone(task: Task) {
    task.done()
    storeModel.orderTasks()
    message.success("任务已完成")
    save()
  }

  function getDoingStatusTips(task: Task): string {
    if (task.isDoing()) {
      return "施工中⛑ "
    } else {
      return ""
    }
  }

  function getConsumed(task: Task): string {
    let time = Format.formatDuration(task.getAllDuration());
    if (Utils.isStringEmpty(time)) {
      return ""
    }
    return `进行${time}(${task.doingDurs.length}次) `
  }

  function getDeadline(task: Task): string {
    let deadline = task.getActualDeadline()
    if (deadline <= 0) {
      return ""
    }
    const leftTime = deadline - Utils.getTimestamp();
    if (leftTime <= 1000) {
      return `已超期😱 `
    }
    let time = Format.formatDuration(leftTime);
    return `剩余${time} `
  }

  function getConsumeExpected(task: Task): string {
    if (task.getActualExpectConsume() <= 0) {
      return ""
    }

    let left = task.getLeftExpectConsumeTime()
    if (left <= 0) {
      return `超出${Format.formatDuration(-left)}`
    } else {
      return `还需${Format.formatDuration(left)}`
    }
  }

  function getShowTips(task: Task): string {
    let result = "  " + getDoingStatusTips(task) + getConsumed(task) + getDeadline(task) + getConsumeExpected(task)
    return result
  }

  function onChooseDeadline(value: any, task: Task) {
    // 整了半天，原来直接 valueOf() 就行，百度了半天
    // if(value instanceof Moment){
    //   let moment:Moment = value
    //   console.log('onOk: ', moment.);
    // }
    // console.log('onOk: ', value.valueOf());

    task.deadlines.push(value.valueOf())
  }

  function getDeadlineTime(task: Task): any {
    const deadline = task.getActualDeadline();
    if (deadline > 0) {
      return moment(deadline)
    }

    return undefined
  }

  function formartLevel(level: number): string {
    switch (level) {
      case 1:
        return "❗"
      case 2:
        return "🎏"
      case 3:
        return "3️⃣"
      case 4:
        return "4️⃣"
      case 5:
        return "5️⃣"
      default:
        return ""
    }
  }

  return (
    <Container2>
      <List
        size="small"
        style={{ backgroundColor: 'white', margin: "1%" }}
        bordered
        dataSource={storeModel.getTasks(DateUtils.getMyCurrentDayDur(currentDayOffset))}
        renderItem={task => (
          <NoteItem choosed={task.isSameTask(currentTask)} key={task.id} onClick={(e) => onChooseNote(e, task)} isDone={task.isDone()}>
            <ButtonGroup>
              {/* 点击整行就是查看且开始，view是只查看不开始。暂时用不着吧 */}
              {/* <Button type="primary" size="small">view</Button> */}
              <Button size="small" onClick={(e) => deleteTask(e, task)}>D</Button>
              {/* <Button type="primary" size="small" onClick={(e) => startTask(e, task)}>Stop</Button> */}
              <Button size="small" onClick={(e) => doneTask(e, task)}>Done</Button>
              <Button size="small" onClick={(e) => startTask(e, task)}>Start</Button>
              <span style={{ opacity: 0.5 }}>

              </span>
              <Dropdown  overlay={getMenu(task)}>
                <a style={{ paddingLeft: 5 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                  {formartLevel(task.level)} <DownOutlined />
                </a>
              </Dropdown>
            </ButtonGroup>
            {/* {task.id} */}
            <DatePicker showTime defaultValue={getDeadlineTime(task)} onOk={value => onChooseDeadline(value, task)} size="small" />
            {getShowTips(task)}
            <NoteItemInput key={task.id} task={task} />
          </NoteItem>
        )}
      />
    </Container2>
  );
}

