import 'reflect-metadata'
import React, { useState } from 'react';
import styled from 'styled-components';
import stores from '../stores';
import { StoreModel, Duration, TimeLine } from '../model/model'
import DateUtils from '../utils/DateUtils'
import Format from '../utils/Format'


const Div = styled.div`

`

const TimeLineLayout: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };

  // React.useEffect(() => {
  //   console.log("app useEffect")
  // },[])

  function getTimelines(): string{
    return storeModel.getTasks(DateUtils.getMyCurrentDayDur(0))
    .flatMap((task) => task.note.timeLines).sort((timeline) => timeline.timeStamp)
    .reverse().map((timeline) => {
      if (timeline.isDivder) {
          return ""
      } else {
          return `${Format.formatTimeInSecond(timeline.timeStamp)} ${timeline.content} \n`
      }
    }).join("")
  }

  return (
    <Div style={{ overflow: "scroll", whiteSpace: "pre-wrap", margin:"5px" }}>
      {getTimelines()}
    </Div>
  );
}

export default TimeLineLayout;
