import 'reflect-metadata'
import React, { useState } from 'react';
import styled from 'styled-components';
import stores from '../stores';
import { StoreModel, Duration, TimeLine } from '../model/model'
import DateUtils from '../utils/DateUtils'
import Format from '../utils/Format'


const Div = styled.div`
height: 45vh;
overflow: scroll;
white-space: pre-wrap;
padding: 5px;
background-color:rgba(194, 226, 255, 0.795);

::-webkit-scrollbar {
            -webkit-appearance: none;
            width: 7px;
        }
        ::-webkit-scrollbar-thumb {
            border-radius: 4px;
            background-color: rgba(0, 0, 0, .5);
            box-shadow: 0 0 1px rgba(255, 255, 255, .5);
        }
`

const TodayWorkConatiner: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };

  function getTimelines(): string {
    return storeModel.getCurrentDayDoingTasks(DateUtils.getMyCurrentDayDur(0))
      .sort((a, b) => {
        if (a.lastStartTime() > b.lastStartTime()) {
          return -1
        } else if (a.lastStartTime() < b.lastStartTime()) {
          return 1
        } else {
          return 0
        }
      }
      )
      .map((task) => {
        return `${task.title} `
      }).join("\n")
  }

  return (
    <Div>
      {/* <div style={{ fontWeight: "bold" }}>【最近任务】</div> */}

      <List
        size="small"
        style={{ backgroundColor: 'white', margin: "1%" }}
        bordered
        dataSource={storeModel.getTasks(DateUtils.getMyCurrentDayDur(currentDayOffset))}
        renderItem={task => (
          <NoteItem choosed={task.isSameTask(currentTask)} key={task.id} onClick={(e) => onChooseNote(e, task)} isDone={task.isDone()}>

            <Row>
              <Col flex="1 1 auto">
                <NoteItemInput key={task.id} task={task} />
              </Col>
              <Col flex="0 1 auto">

                {getShowTips(task)}
                <Dropdown overlay={getMenu(task)}>
                  <a style={{ paddingLeft: 5 }} className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                    {formartLevel(task.level)} <DownOutlined />
                  </a>
                </Dropdown>

                <Button onClick={(e) => startTask(e, task)}>Start</Button>

              </Col>
            </Row>

          </NoteItem>
        )}
      />
    </Div>
  );
}

export default TodayWorkConatiner;
