    if (task.isDoing()) {
      // todo è‡ªåŠ¨æ¢å¤
      message.error(`ä»»åŠ¡å¼‚å¸¸ä¸­æ–­ï¼Œé‡æ–°å¼€å§‹ã€‚ä¸Šæ¬¡å¼€å§‹æ—¶é—´ï¼š${Format.formatTimeInMs(task.lastStartTime())}`)
    }

    if (currentDoingTask != undefined) {
      currentDoingTask.stop()
    }

    task.start()
    setCurrentDoingTask(task)
    storeModel.orderTasks()
    save()
  }


  function formartLevel(level: number): string {
    switch (level) {
      case 1:
        return "ğŸŒŸ"
      case 2:
        return "ğŸ"
      case 3:
        return "3ï¸âƒ£"
      case 4:
