import 'reflect-metadata'
import React, { useState } from 'react';
import styled from 'styled-components';
import stores from '../stores';
import { List } from 'antd';
import { StoreModel, Duration, TimeLine } from '../model/model'
import DateUtils from '../utils/DateUtils'
import Format from '../utils/Format'


const Div = styled.div`
height: 55vh;
overflow-y: scroll;
white-space: pre-wrap;
padding: 5px;
background-color:rgb(227, 233, 231);

::-webkit-scrollbar {
            -webkit-appearance: none;
            width: 7px;
        }
        ::-webkit-scrollbar-thumb {
            border-radius: 4px;
            background-color: rgba(0, 0, 0, .5);
            box-shadow: 0 0 1px rgba(255, 255, 255, .5);
        }
`

const TimeLineLayout: React.FC = () => {
  const { storeModel } = stores.useStore('mainmodel') as { storeModel: StoreModel };

  // React.useEffect(() => {
  //   console.log("app useEffect")
  // },[])

  function getTimelines(): string {
    return storeModel.getTasks(DateUtils.getMyCurrentDayDur(0))
      .flatMap((task) => task.note.timeLines.map((timeline) => {
        return timeline.getTitled(task.title)
      })).sort((a, b) => {
        if (a.timeStamp > b.timeStamp) {
          return -1
        } else if (a.timeStamp < b.timeStamp) {
          return 1
        } else {
          return 0
        }
      }
      )
      .map((timeline) => {
        if (timeline.isDivder) {
          return ""
        } else {
          return `${Format.formatTimeInSecond2(timeline.timeStamp)} ${timeline.content} \n`
        }
      }).join("")
  }

  function getCurrentDayDoingTasks(): Task[] {
    return storeModel.getTasks(DateUtils.getMyCurrentDayDur(0))
    .flatMap((task) => task.note.timeLines.map((timeline) => {
      return timeline.getTitled(task.title)
    })).sort((a, b) => {
      if (a.timeStamp > b.timeStamp) {
        return -1
      } else if (a.timeStamp < b.timeStamp) {
        return 1
      } else {
        return 0
      }
    }
    )
  }

  return (
    <Div>
      <List
        size="small"
        bordered
        dataSource={getCurrentDayDoingTasks()}
        renderItem={task => (
          <NoteItem choosed={task.isSameTask(currentTask)} key={task.id} onClick={(e) => setCurrentTask(task)}>
            {task.title} <span style={{ color:"#0295d2" }}>{Format.formatDuration3(task.getAllDurationInDur(DateUtils.getMyCurrentDayDur(0)))}</span>
          </NoteItem>
        )}
      />
    </Div>
  );
}

export default TimeLineLayout;
